import { PublicClientApplication } from '@azure/msal-browser';
import { UserAgentApplication } from 'msal'
import { APP_ID, REDIRECT_URI, SCOPES } from '../config/environment';

const MSAL_CONFIG = {
  auth: {
    clientId: APP_ID,
    authority: 'https://login.microsoftonline.com/common',
    redirectUri: REDIRECT_URI
  },
  cache: {
    cacheLocation: "sessionStorage",
    storeAuthStateInCookie: true
  }
};

const VOLVO_CONFIG = {
  auth: {
    clientId: APP_ID,
    authority: 'https://login.microsoftonline.com/common',
    redirectUri: REDIRECT_URI,
    postLogoutRedirectUri: REDIRECT_URI,
  }
}

// export const azureProvider = new PublicClientApplication(MSAL_CONFIG);
export const azureProvider = new UserAgentApplication(VOLVO_CONFIG);

export const scopeRequestConfig = {
  scopes: SCOPES.split(',')
}

export const getAccessToken = async () => {
  const account = azureProvider.getAccount();
  try {
    if (!account) throw new Error('login_required');
    const silentResult = await azureProvider.acquireTokenSilent(scopeRequestConfig);

    return silentResult.accessToken;
  } catch (err) {
    if (isInteractionNeeded(err)) {
      azureProvider.loginRedirect(scopeRequestConfig)
    } else {
      throw err;
    }
  }
};

