import { UserAgentApplication } from 'msal'
import { APP_ID, REDIRECT_URI, SCOPES } from '../config/environment';

const MSAL_CONFIG = {
  auth: {
    clientId: APP_ID,
    redirectUri: REDIRECT_URI,
  }
};

export const azureProvider = new UserAgentApplication(MSAL_CONFIG);
export const scopeRequestConfig = {
  scopes: SCOPES.split(',')
}

export const getAccessToken = async () => {
  const account = azureProvider.getAccount();

  console.log(scopeRequestConfig);
  // console.log(azureProvider);
  try {
    if (!account) throw new Error('login_required');
    console.log('getting token');
    const silentResult = await azureProvider.acquireTokenSilent(scopeRequestConfig);
    return silentResult.accessToken;
  } catch (err) {
    console.log('is error');
    if (isInteractionNeeded(err)) {
      console.log('is one of the errors');
      try {
        const interactiveResult = await azureProvider.acquireTokenPopup({
          scopeRequestConfig
        });
        console.log(interactiveResult);

        return interactiveResult.accessToken;
      } catch (e) {
        console.log(e);
      }



    } else {
      throw err;
    }
  }
};

const isInteractionNeeded = (error) => {
  if (!error.message || error.message.length <= 0) {
    return false;
  }

  return (
    error.message.indexOf('consent_required') > -1 ||
    error.message.indexOf('interaction_required') > -1 ||
    error.message.indexOf('login_required') > -1 ||
    error.message.indexOf('no_account_in_silent_request') > -1
  );
};

export const getUserProfile = async () => {
  try {
    const accessToken = await getAccessToken(scopeRequestConfig);
    if (accessToken) {
      return {
        isAuthenticated: true,
        user: {},
        error: {message: "Access token:", debug: accessToken}
      };
    }
  } catch (err) {
    return {
      isAuthenticated: false,
      user: {},
      error: normalizeError(err)
    };
  }
};

const normalizeError = (error) => {
  let normalizedError = {};

  if (typeof (error) === 'string') {
    const errParts = error.split('|');
    normalizedError = errParts.length > 1 ?
      {message: errParts[1], debug: errParts[0]} :
      {message: error};
  } else {
    normalizedError = {
      message: error.message,
      debug: JSON.stringify(error)
    };
  }
  return normalizedError;
};
