import { PublicClientApplication } from '@azure/msal-browser';
import { APP_ID, REDIRECT_URI } from '../config/environment'

export const MSAL_CONFIG = {
  auth: {
    clientId: APP_ID,
    redirectUri: REDIRECT_URI
  },
  cache: {
    cacheLocation: "sessionStorage",
    storeAuthStateInCookie: true
  }
};

const scopes = [
  'user.read',
  'mailboxsettings.read',
  'calendars.readwrite'
]

export const azureProvider = new PublicClientApplication(MSAL_CONFIG);

const normalizeError = (error) => {
  let normalizedError = {};

  if (typeof (error) === 'string') {
    const errParts = error.split('|');
    normalizedError = errParts.length > 1 ?
      {message: errParts[1], debug: errParts[0]} :
      {message: error};
  } else {
    normalizedError = {
      message: error.message,
      debug: JSON.stringify(error)
    };
  }
  return normalizedError;

};

const isInteractionNeeded = (error) => {
  if (!error.message || error.message.length <= 0) {
    return false;
  }

  return (
    error.message.indexOf('consent_required') > -1 ||
    error.message.indexOf('interaction_required') > -1 ||
    error.message.indexOf('login_required') > -1 ||
    error.message.indexOf('no_account_in_silent_request') > -1
  );
};

export const getAccessToken = async (scopes) => {
  try {
    const silentResult = await azureProvider.acquireTokenSilent({
      scopes: scopes,
      account: allAccounts[0]
    });

    return silentResult.accessToken;

  } catch (err) {
    if (isInteractionNeeded(err)) {
      const interactiveResult = await azureProvider.acquireTokenPopup({
        scopes: scopes
      });

      return interactiveResult.accessToken;
    } else {
      throw err;
    }
  }
};

export const getUserProfile = async () => {
  try {
    const accessToken = await getAccessToken(scopes);
    if (accessToken) {
      return {
        isAuthenticated: true,
        user: {},
        error: {message: "Access token:", debug: accessToken}
      };
    }
  } catch (err) {
    return {
      isAuthenticated: false,
      user: {},
      error: normalizeError(err)
    };
  }
};
