import createDataContext from './createDataContext';
import { getUserProfile, azureProvider, scopeRequestConfig, getAccessToken } from '../utils/security.util';

const TYPES = {
  ADD_ERROR: 'auth/add_error',
  LOGIN: 'auth/login',
  SIGN_OUT: 'auth/signOut',
  CLEAR_ERROR: 'auth/clearError',
}

const authReducer = (state, action) => {
  switch (action.type) {
    case TYPES.ADD_ERROR:
      return {...state, errorMessage: action.payload};
    case TYPES.LOGIN:
      return {isAuthenticated: true, errorMessage: '', token: action.payload};
    case TYPES.CLEAR_ERROR:
      return {...state, errorMessage: ''};
    case TYPES.SIGN_OUT:
      return {isAuthenticated: false, token: null, errorMessage: ""};
    default:
      return state;
  }
};

const signIn = ( dispatch ) => async () => {
 try {
   const firstTimeRefresh = getAccessToken()
   console.log(firstTimeRefresh);
 } catch (err) {
   console.log(err);
 }
};

export const {Provider, Context} = createDataContext(
  authReducer,
  {signIn},
  {isAuthenticated: false, token: null, errorMessage: ''}
);
