import { lazy } from 'react';
import { metadata as appOneMetadata } from '@data-portal/app-one';
import { metadata as appTwoMetadata } from '@data-portal/app-two';
import { convertToKebabCase } from './_general.util';

const APP_1 = lazy(() => import('@data-portal/app-one'));
const APP_2 = lazy(() => import('@data-portal/app-two'));

const templates = [APP_1, APP_2]
export const useGetDashboards = () => {
  const listOfLibraries = [appOneMetadata, appTwoMetadata];

  const dashBoardsList = listOfLibraries.map((dashboard, idx) => ({
    title: dashboard.title,
    description: dashboard.description,
    icon: dashboard.icon,
    slug: `/${convertToKebabCase(dashboard.title)}`,
    requiredPermissions: dashboard.requiredPermissions,
    component: templates[`APP_${idx + 1}`]
  }));


  // const dashBoardsList = [
  //   {
  //     title: appOneMetadata.title,
