import createDataContext from './createDataContext';
import { azureProvider, isInteractionNeeded, scopeRequestConfig } from '../utils/security.util';

const TYPES = {
  ADD_ERROR: 'auth/add_error',
  LOGIN: 'auth/login',
  SIGN_OUT: 'auth/signOut',
  CLEAR_ERROR: 'auth/clearError',
  SET_USER: 'auth/setUser'
};

const initialState = {
  isAuthenticated: false,
  token: null,
  errorMessage: '',
  username: null
};

const authReducer = (state, action) => {
  switch (action.type) {
    case TYPES.ADD_ERROR:
      return {...state, errorMessage: action.payload};
    case TYPES.LOGIN:
      return {username: action.payload.name, isAuthenticated: true, errorMessage: '', token: action.payload.token};
    case TYPES.CLEAR_ERROR:
      return {...state, errorMessage: ''};
    case TYPES.SIGN_OUT:
      return initialState;
    case TYPES.SET_USER:
      return {...state, username: action.payload};
    default:
      return state;
  }
};

const signIn = (dispatch) => async () => {
  const allAccounts = await azureProvider.getAllAccounts();

  try {
    if (allAccounts?.length <= 0) throw new Error('login_required');
    const silentResult = await azureProvider.acquireTokenSilent({
      scopeRequestConfig,
      account: allAccounts[0]
    });

    dispatch({
      type: TYPES.LOGIN,
      payload: {
        token: silentResult.accessToken,
        name: allAccounts[0].name
      }
    });
  } catch (err) {
    if (isInteractionNeeded(err)) {
      const interactiveResult = await azureProvider.loginPopup(scopeRequestConfig);
      dispatch({
        type: TYPES.LOGIN,
        payload: interactiveResult.accessToken
      });
    } else {
      throw err;
    }
  }
};

