};

const signOut = (dispatch) => async () => {
  await azureProvider.logout();

  dispatch({
    type: TYPES.SIGN_OUT
  });
};


// Todo: signout from FE

export const {Provider, Context} = createDataContext(
  authReducer,
  {signIn, getProfile, signOut},
  initialState
);
