import React, { lazy, Suspense, useContext, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { Context as AuthContext } from '../context/AuthContext';
import { useUserHasPermission } from '../utils/useUserHasPermission.util';
import { PERMISSIONS } from '../consts/permissions.consts';
import { Dashboard } from './dashboard/Dashboard';
import { GenericTemplate } from './genericTemplate';
import { metadata as appOneMetadata } from '@data-portal/app-one';
import { metadata as appTwoMetadata } from '@data-portal/app-two';
import { convertToKebabCase } from '../utils/_general.util';

const GHubProfile = lazy(() => import('@data-portal/app-one'));
const Counter = lazy(() => import('@data-portal/app-two'));

const dashboards = [
  {
    link: convertToKebabCase(appOneMetadata.title),
    component: <GHubProfile/>
  }, {
    link: convertToKebabCase(appTwoMetadata.title),
    component: <Counter/>
  },
];

console.log(dashboards);
export const App = () => {
  const {signIn} = useContext(AuthContext);

  useEffect(() => {
    signIn();
  }, []);

  console.log(useUserHasPermission(PERMISSIONS.GUEST));

  return (
    <Router>
      <Suspense fallback={<div>Loading...</div>}>
        <Switch>
          <Route exact path="/" component={Dashboard}/>
          {dashboards.map(dashboard => (
            <Route
              key={dashboard.link}
              exact
              path={dashboard.link}
              component={GenericTemplate}
            />
          ))}
        </Switch>
      </Suspense>
    </Router>
  );
};

