import React, { lazy, Suspense, useState } from 'react';
import { Grid, Icon, Menu, Segment, Sidebar } from 'semantic-ui-react';
import { Dashboard } from '../dashboard/Dashboard';
import BLUE_LOGO from '../../assets/icons/assets-logo-icon.png';
import { useGetDashboards } from '../../utils/useGetDashboards';

const GHubProfile = lazy(() => import('@data-portal/app-one'));
const Counter = lazy(() => import('@data-portal/app-two'));

const Segments = {
  HOME: 'HOME',
  APP_ONE: 'APP_0',
  APP_TWO: 'APP_1',
};

export const SideNavbar = () => {
  const [visibleSegment, setVisibleSegment] = useState(Segments.HOME);
  const { dashBoardsList } = useGetDashboards()

  console.log(dashBoardsList);
  // const navEntries = dashBoardsList.reduce((acc, Comp, idx) => ({
  //   ...acc,
  //   [`APP_${idx + 1 }`]: acc.component
  // }))
  const navEntries = dashBoardsList.map(dashboard => ({
    ...dashboard,
    [dashboard.title]: dashboard.component
  }))

  console.log(navEntries);
  console.log(visibleSegment);

  const renderSegments = (name) => ({
    [Segments.HOME]: <Dashboard/>,
    ...navEntries
  })[name];

  return (
    <Grid columns={1}>
      <Grid.Column>
        <Sidebar.Pushable as={Segment} style={{minHeight: '100vh'}}>
          <Sidebar
            as={Menu}
            animation='overlay'
            direction='left'
            icon='labeled'
            inverted
            vertical
            visible={true}
            width='thin'
          >
            <Menu.Item className="side-navbar-item" style={{display: 'flex !important'}} as='a'
                       onClick={() => setVisibleSegment(Segments.HOME)}
                       active={visibleSegment === Segments.HOME}>
              <img className="side-navbar-item-logo" src={BLUE_LOGO} alt="volvo"/>
              <span className="side-navbar-item-title">Volvo</span>
            </Menu.Item>
            <Menu.Item as='a' onClick={() => setVisibleSegment(Segments.APP_ONE)}
                       active={visibleSegment === Segments.APP_0}>
              <Icon name='font'/>
              Npm package
            </Menu.Item>
            <Menu.Item as='a' onClick={() => setVisibleSegment(Segments.APP_TWO)}
                       active={visibleSegment === Segments.APP_1}>
              <Icon name='bold'/>
              Npm package
            </Menu.Item>
          </Sidebar>
          <Sidebar.Pusher>
            <Suspense fallback={<div>Loading...</div>}>
              <div className="h-main-segment">
                {renderSegments(visibleSegment)}
              </div>
            </Suspense>
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </Grid.Column>
    </Grid>
  );
};

