import React, { useContext, useEffect } from 'react';
import { useHistory } from "react-router-dom";
import PropTypes from 'prop-types';
import { Context as NotificationsContext } from '../../context/NotificationsContext';
import { Context as AuthContext } from '../../context/AuthContext';
import { usePrivateRoute, userHasPermission } from '../../utils/useUserHasPermission.util';

export const GenericTemplate = ({children, dashboard }) => {
  const history = useHistory();
  const {state: {role}} = useContext(AuthContext);
  const privateRoute = usePrivateRoute(dashboard.requiredPermissions, role)
  const {setNotification} = useContext(NotificationsContext);

  return (
    <div className="h-main-segment">
      {children}
    </div>
  );
};

GenericTemplate.propTypes = {
  dasboard: PropTypes.shape({
    title: PropTypes.string,
    description: PropTypes.string,
    requiredPermissions: PropTypes.array,
    icon: PropTypes.string,
  })
};
